INSERT INTO ponymaze.plot (id, plot, event) VALUES (1, 'Pinky Pie wpadła tutaj przez przypadek. Była ciekawa co znajduje się w budynku, który oglądała od jakiegoś czasu.
INSERT INTO ponymaze.plot (id, plot, event) VALUES (2, 'Miejsce od środka było przerażające, zniszczone, pełne dziwnych dźwięków. Więc Zaczęła szukać innego wyjścia.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (3, 'Było tu mnóstwo ciemnych i przerażających korytarzy, więc to trudne zadanie.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (4, 'O leki! Może dzięki nim utrzyma się w dobrej kondycji.', 'Leki');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (5, 'Sprawdzała każdy pokój, znów widziała straszne rzeczy, nawet salę operacyjną z pełnym sprzętem.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (6, 'Chwilami Pinky miała wrażenie, że coś rusza się w cieniu, więc od razu uciekała dalej.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (7, 'Przez ciągłe uciekanie i ciemność, miał wrażenie, że coraz bardziej się gubi.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (8, 'Chodziła od sali do sali, zaglądała przez każde okno, ale nie mogła znaleźć żadnego rozwiązania.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (9, 'W jednym pomieszczeniu przestraszyła się sztucznego (albo nie?) kościotrupa. Żeby wyjrzeć przez okno, musiał przejść obok niego. W drodze do okna, przechodząc obok przerażającej postaci, Pinky miał wrażenie, że kościotrup poruszył ręką. Mimo to, chciała wyjrzeć przez okno. Gdy minęła atrapę, poczuła jak kościste paliczki zaciskają się na ogonie.', 'Duch');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (10, 'Chodziła po korytarzu tak długo, że już bolały ją kopytka, w dodatku zmęczyła ją ucieczka przed kościotrupem.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (11, 'Chciała odpocząć, ale jeszcze bardziej chciała się stąd wydostać. Mimo to, usiadła w jednej sali, by trochę odpocząć i zastanowić się nad sytuacją.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (12, 'Z każdym krokiem miała wrażenie, że miejsce było z chwili na chwilę coraz bardziej przerażające.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (13, 'Pinky Pie miała nadzieję, że uda jej się zeskoczyć na któreś z drzew otaczających szpital. Ale dotarła na taras. Może jak znajdzie coś, dzięki czemu się opuści w dół i ucieknie stąd!', 'Taras');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (14, 'Sprawdzała każdy pokój, znów widziała straszne rzeczy, nawet salę operacyjną z pełnym sprzętem.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (15, 'Pinky Pie wzdrygnęła się na widok szpitalnego łóżka z pasami i poszła dalej.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (16, 'W tym miejscu zauważyła dziwną, białą poświatę, która zdawała się na nią patrzeć.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (17, 'Było tu mnóstwo ciemnych i przerażających korytarzy, więc to trudne zadanie. Przez ciągłą czujność, którą wymuszała ciemność, miała wrażenie, że coraz bardziej się gubi.', '');
INSERT INTO ponymaze.plot (id, plot, event) VALUES (18, 'Rozejrzała się po pokoju, szukając rozwiązania. Na starym łóżku leżało prześcieradło. Może, jeśli będzie podobna do duchów, to te go zignorują? Nie miała innego wyjścia, niż to sprawdzić, więc wzięła zakurzony materiał. Z pomocą odłamków szkła leżących na podłodze, zrobił otwory na oczy i zarzucił na siebie prześcieradło.', 'Koc');
